		events = new emitter(),
		proc,
		engine,
		data = '',
		tmpDir,
		options;

	// options can be omitted
	if (typeof opts === 'function') {
		callback = opts;
		opts = {};
	}

	debug('nodejs %s', process.version);
	debug('phantomas v%s installed in %s', VERSION, phantomas.path);
	debug('URL: <%s>', url);
	debug('Options: %s', JSON.stringify(opts));

