// handle --config (issue #209)
program.setConfigFile('config');

// parse it
options = program.parse(process.argv);

// show version number
if (options.version === true) {
	console.log('phantomas v%s', phantomas.version);
	process.exit(0);
}

// show help
if (options.help === true) {
	program.showHelp();
	process.exit(0);
}

// handle URL passed without --url option (#249)
if (typeof options._[2] === 'string') {
	options.url = options._[2];
}

// --url is mandatory -> show help
if (typeof options.url !== 'string' && typeof options.config === 'undefined') {
	program.showHelp();
	process.exit(255);
}

url = options.url;
runs = parseInt(options.runs) || 1;

delete options.url;
delete options._;
delete options.$0;

// handle --no-foo options
options['no-externals'] = options.externals === false;
delete options.externals;

// handle --progress option
var bar;

if (options.progress === true) {
	options.verbose = false;

	bar = new ProgressBar('[:bar] :percent :etas', {
		total: 100 * runs,
		width: 50
	});
}

// add env variable to turn off ANSI colors when needed (#237)
// suppress this behaviour by passing --colors option (issue #342)
if (!process.stdout.isTTY && (options.colors !== true)) {
	debug('ANSI colors turned off');
	process.env.BW = 1;
}

// perform a single run
function task(callback) {
	// spawn phantomas process
	var child = phantomas(url, options, function(err, data, results) {
		callback(
			null, // pass null even in case of an error to continue async.series processing (issue #380)
			[err, results]
		);
	});

	child.on('progress', function(progress, inc) {
		if (bar) {
			bar.tick(inc);
		}
	});

	// pipe --verbose messages to stderr
	child.stderr.pipe(process.stderr);
}

// @see https://github.com/caolan/async#seriestasks-callback
var series = [];

debug('Preparing %d run(s)...', runs);

for (var r = 0; r < runs; r++) {
	series.push(task);
}

async.series(
	series,
	function(err, results) {
		var debug = require('debug')('phantomas:runs'),
			needDrain,
			reporter,
			res;

		// results is a collection of the following entries:
		// [ Error from the run, Results object]
		debug('results [%d]: %j', results.length, results);

		results = results.map(function(item, idx) {
			var error = item[0],
				result = item[1];

			// detect errors in run results (issue #380)
			if (error instanceof Error) {
				debug('Run #%d did not complete - err #%d', idx + 1, error.message);
				err = error.message;
			}

			// return results wrapper (if possible) - #528
			return result;
		});

		// filter out "broken" results (issue #366)
		results = results.filter(function(item) {
			return typeof item !== 'undefined';
		});

		debug('err: %j', err);

		// this function is called when phantomas is done with all runs
		function doneFn() {
			// pass error code from PhantomJS process
			debug('Exiting with code #%d', err || 0);
			process.exit(err);
		}

		if (typeof results[0] !== 'undefined') {
			// we have at least one result - reset the error flag when in multiple runs mode (#380)
			if (err > 250 && runs > 1) {
				err = null;
			}

			// process JSON results by reporters
			debug('%d of %d run(s) completed with exit code #%d', results.length, runs, err || 0);

			// normalize results in a single run mode
			if (runs === 1) {
				results = results[0];
			}

			reporter = require('../core/reporter')(results, options);

			debug('Calling a reporter...');

			// pass a function that reporter should call once done
			res = reporter.render(doneFn);

			// reporter returned results, otherwise wait for doneFn to be called by reporter
			if (typeof res !== 'undefined') {
				needDrain = !process.stdout.write(res);

				// If a stream.write(chunk) call returns false, then the 'drain' event will indicate when it is appropriate to begin writing more data to the stream.
				// @see #596
				if (needDrain) {
					debug('Need to wait for stdout to be fully flushed...');
					process.stdout.on('drain', doneFn);
				} else {
					doneFn();
				}
			} else {
				debug('Waiting for the results...');
			}
		} else {
			doneFn();
		}
	}
);

/**
 * Extends optimist with support for JSON/YAML config file
 *
 * Pass a path to the config file using --config option
 *
 * @see https://github.com/substack/node-optimist
 * @see https://github.com/substack/minimist
 */
var debug = require('debug')('optimist:config'),
	fs = require('fs'),
	optimist = require('optimist')(process.argv.slice(2)),
	ansistyles = require('ansistyles'),
	yaml = require('js-yaml');

function parseConfigFile(configFileName) {
	var raw,
		configMode,
		configParsed;

	debug('using "%s" config file', configFileName);

	// check the existance of the config file
	if (!fs.existsSync(configFileName)) {
		throw 'Config file "' + configFileName + '" cannot be found!';
	}

	// JSON or YAML?
	configMode = /\.yaml$/.test(configFileName) ? 'YAML' : 'JSON';

	// parse it
	try {
		raw = fs.readFileSync(configFileName).toString();

		switch (configMode) {
			case 'YAML':
				configParsed = yaml.safeLoad(raw);
				break;

			case 'JSON':
				configParsed = JSON.parse(raw);
				break;
		}
	} catch (e) {
		debug('%s config file parsing failed (%s)', configMode, e);
		throw configMode + '%s config file parsing failed (' + e + ')';
	}

	// convert nested objects into options
	// {'foo':{'bar': 1}} -> --foo--bar 1
	Object.keys(configParsed).forEach(function(key) {
		var value = configParsed[key];

		if (typeof value === 'object' && !Array.isArray(value)) {
			delete configParsed[key];

			// remove plural suffix
			key = key.replace(/s$/, '');

			debug('unwrapping --%s-* options...', key);

			Object.keys(value).forEach(function(subKey) {
				configParsed[key + '-' + subKey] = value[subKey];
			});
		}
	});

	debug('%s config file parsed: %j', configMode, configParsed);
	return configParsed;
}

// extend optimist
var origParse = optimist.parse;

optimist.parse = function(args) {
	var options = origParse(args);
	debug('parse: %j', args);

	// check the config file
	var configFileName = this.configFileParam && options[this.configFileParam],
		configParsed;

	// if --config is not provided, quit
	if (!configFileName) {
		debug('options: %j', options);
		return options;
	}

	configParsed = parseConfigFile(configFileName);

	// apply the rest of the options provided via command line
	Object.keys(configParsed).forEach(function(key) {
		// check if given option was not provided via command line
		if (args.indexOf('--' + key) > -1) {
			debug('--%s option provided via command line, will skip the value from config file', key);
			return;
		}

		options[key] = configParsed[key];
	});

	// cleanup
	delete options[this.configFileParam];

	debug('options: %j', options);
	return options;
};

optimist.setConfigFile = function(configFileParam) {
	this.configFileParam = configFileParam;
	return this;
};

var _describe = optimist.describe,
	_help = optimist.help,
	headers = [],
	nextHeader;

optimist.header = function(s) {
	nextHeader = s;
	return this;
};
optimist.describe = function(opt) {
	if (nextHeader) {
		headers.push({
			label: nextHeader,
			before: opt
		});
		nextHeader = null;
	}
	return _describe.apply(optimist, arguments);
};
optimist.help = function() {
	var text = _help.apply(optimist, arguments),
		i, s, p, opt_header = "\nOptions:",
		opt_header_len = opt_header.length;
	//console.log(text,headers);
	for (i = 0; i < headers.length; i++) {
		s = "\n  --" + headers[i].before;
		p = text.indexOf(s);
		if (p != -1) {
			s = "\n" + ansistyles.bright(headers[i].label) + ":";
			text = text.substr(0, p) + s + text.substr(p);
			if (text.substr(p - opt_header_len, opt_header_len) == opt_header) {
				text = text.substr(0, p - opt_header_len) + text.substr(p);
			}
		}
	}
	return text;
};

module.exports = optimist;

/**
 * Defines PhantomJS 2.x engine
 *
 * @see https://github.com/macbre/phantomas/issues/488
