/**
 * Simple IPC implementation using JSON-encoded messages sent over stdout stream
 *
 * Implements consumer of the data (for nodejs environment)
 */
'use strict';

var debug = require('debug')('phantomas:ipc'),
	emitter = require('events').EventEmitter,
	SEPARATOR = "\xFF\xFF";

function ipc(stream) {
	/* jshint validthis: true */
	this.stream = stream;
	this.events = new emitter();

	this.init();
}

ipc.prototype.setEventEmitter = function(emitter) {
	this.events = emitter;
};

ipc.prototype.init = function() {
	var self = this,
		buffer = '';

	this.stream.on('data', function(data) {
		buffer += data.toString().trim();

		// check for the separator at the of the buffer - parse the data
		if (buffer.substr(-2) === SEPARATOR) {
			self.parse(buffer);
			buffer = '';
		}
	});
};

ipc.prototype.parse = function(data) {
	// split by separator
	data.split(SEPARATOR).forEach(function(msg) {
		msg = msg.trim();

		if (msg === '') return;

		try {
			msg = JSON.parse(msg);
			debug('%s: %j', msg.event, msg.data);

			// send event name and the rest of the data
			var args = msg.data;
			args.unshift(msg.event);

			this.events.emit.apply(this.events, args);

			// send generic "_msg" event for each message received (issue #354)
			this.events.emit('_msg', args);
		} catch (e) {
			// send PhantomJS errors to stderr to ease debugging of issues like #302
			debug('message parsing failed: "%s"', msg);
			process.stderr.write(msg + "\n");
		}
	}, this);
};

ipc.prototype.on = function(event, fn) {
	this.events.on(event, fn);
};

module.exports = ipc;

/**
 * Generic JSON results formatter
 */
'use strict';

module.exports = function(results) {
	var res = {
		generator: results.getGenerator(),
		url: results.getUrl(),
		metrics: results.getMetrics(),
		offenders: results.getAllOffenders(),
		asserts: false
	};

	// add asserts
	var asserts = results.getAsserts();

	if (Object.keys(asserts).length > 0) {
		res.asserts = asserts;
	}

	return JSON.stringify(res);
};

/**
 * Simple IPC implementation using JSON-encoded messages sent over stdout stream
 *
 * Implements producer of the data (for PhantomJS environment)
 */
'use strict';

var stream = require('system').stdout,
	SEPARATOR = "\xFF\xFF";

function ipc(event) {
	/* jshint validthis: true */
	this.event = event;
}

ipc.prototype.push = function() {
	stream.writeLine(JSON.stringify({
		event: this.event,
		data: Array.prototype.slice.apply(arguments)
	}));
	stream.writeLine(SEPARATOR);
};

module.exports = ipc;

/**
 * Simple logger (using both file and console)
 */
'use strict';

module.exports = function(logFile, params) {
	var colors = require('../lib/ansicolors'),
		styles = require('ansistyles'),
		fs = require('fs'),
		ipc = new(require('./ipc'))('log'),
		beVerbose = params.beVerbose === true,
		beSilent = params.beSilent === true,
		stream;

	if (logFile !== '') {
		// use an absolute path
		logFile = fs.absolute(logFile);
		log("Logging to " + logFile);

		// set up a stream to be used for logging
		stream = fs.open(logFile, 'w');
	}

	function echo(msg) {
		if (!beSilent) {
			console.log(msg);
		}
	}

	function log(msg) {
		var ts = (new Date()).toJSON().substr(11, 12);

		// format a message
		msg = (typeof msg === 'object') ? JSON.stringify(msg) : msg.toString().trim();

		// log to the console (--verbose)
		if (beVerbose) {
			var consoleMsg = msg;

			// error!
			if (/!$/.test(consoleMsg) || /Error:/.test(consoleMsg)) {
				consoleMsg = colors.brightRed(styles.bright(consoleMsg));
			}
			// label: message
			else if (/^(.*): /.test(consoleMsg)) {
				var idx = consoleMsg.indexOf(': ') + 1;
				consoleMsg = colors.brightGreen(consoleMsg.substr(0, idx)) + colors.brightBlack(consoleMsg.substr(idx));
			}
			// the rest
			else {
				consoleMsg = colors.brightBlack(consoleMsg);
			}

			if (!beSilent) {
				ipc.push(ts + ' ' + consoleMsg);
			}
		}

		// log to the file (--log)
		if (stream) {
			stream.writeLine(ts + ': ' + msg);
			stream.flush();
		}
	}

	// public API
	return {
		echo: echo,
		log: log
	};
};

/**
 * Helper functions for string formatting
 */
'use strict';

function lpad(str, len) {
	var fill;
	str = typeof str !== 'undefined' ? str : '-';

	fill = new Array(Math.max(1, len - str.toString().length + 1)).join(' ');
	return fill + str;
}

function rpad(str, len) {
	var fill;
	str = typeof str !== 'undefined' ? str : '-';

	fill = new Array(Math.max(1, len - str.toString().length + 1)).join(' ');
	return str + fill;
}

exports.lpad = lpad;
exports.rpad = rpad;

/**
 * Reporters factory
 *
 * All reporters are defined in /reporters directory
 * and are provided with run results and command line options
 */
'use strict';

//convenience function to hide the dirty import logic
function _requireReporter(reporterName) {
	var reporter,
		reporterPath = 'phantomas-reporter-' + reporterName;

	try {
		reporter = require(reporterPath);
	} catch (ex) {
		//external reporter doesn't exist yet, try as a "local" reporter
		reporterPath = '../reporters/' + reporterName;
		reporter = require(reporterPath);
	}

	return reporter;
}


module.exports = function(results, options) {
	var debug = require('debug')('phantomas:reporter'),
		reporterName,
		reporterOptions,
		reporter,
		inMultipleMode = false;

	// parse reporter options, examples:
	// -R plain
	// -R csv
	// -R csv:no-header:url:timestamp
	reporterOptions = (options.reporter + '').split(':');
	reporterName = reporterOptions.shift();

	// allow access to options via object.key (for non-numeric values)
	reporterOptions.forEach(function(option) {
		if (!parseInt(option)) {
			reporterOptions[option] = true;
		}
	});

	debug('Setting up %s reporter (options: %j)...', reporterName, reporterOptions);

	if (Array.isArray(results)) {
		debug('Multiple runs mode');
		inMultipleMode = true;
	}

	try {
		reporter = new(_requireReporter(reporterName))(results, reporterOptions, options);
	} catch (ex) {
		debug('Failed: %s', ex);
		throw new Error('Reporter "' + reporterName + '" is not supported!');
	}

