'use strict';

function queue() {
	var remaining = 0,
		jobs = 0,
		doneFn = [];

	function preCheck() {
		if (remaining === false) {
			throw new Error('Can\'t push jobs to completed queue');
		}
	}

	function postCheck() {
		if (remaining === 0 && doneFn.length > 0) {
			doneFn.forEach(function(fn) {
				fn(null, {
					jobs: jobs
				});
			});

			remaining = false;
		}
	}

	// public API
	return {
		// add a job to the queue
		push: function(fn) {
			preCheck();

			remaining++;
			jobs++;

			// pass an anonymous function to the callback
			// call it to mark a job as done
			var called = false;
			fn(function() {
				if (!called) {
					called = true;
					remaining--;
					postCheck();
				}
			});

			return this;
		},

		// call given function when all jobs added to the queue are done
		done: function(fn, scope) {
			doneFn.push(fn.bind(scope));
			postCheck();

			return this;
		}
	};
}

module.exports = queue;

/**
 * Metrics stats implementation to be used by reporters
 * when phantomas is executed in multiple runs mode
 */
'use strict';

var Stats = require('./fast-stats').Stats;

function stats() {
	/* jshint validthis: true */
	// use pushMetrics() to add results for each run
	this.metrics = [];
	this.runs = 0;

	// stats to be calculated
	// @see https://github.com/bluesmoon/node-faststats
	this.stats = {
		min: function(values) {
			return values.range()[0];
		},
		max: function(values) {
			return values.range()[1];
		},
		average: function(values) {
			return values.amean().toFixed(2);
		},
		median: function(values) {
			return values.median().toFixed(2);
		},
		stddev: function(values) {
			return values.stddev().toFixed(2);
		}
	};

	this.statsNames = Object.keys(this.stats);
}

// add key/value collection of metrics values
stats.prototype.pushMetrics = function(metrics) {
	this.metrics.push(metrics);
	this.runs++;
};

// get list of metrics name
stats.prototype.getMetrics = function() {
	if (this.runs > 0) {
		return Object.keys(this.metrics[0]);
	} else {
		return [];
	}
};

// get stats for given metric
stats.prototype.getMetricStats = function(metricName) {
	var i,
		stats = {},
		value,
		values = new Stats();

	for (i = 0; i < this.runs; i++) {
		value = this.metrics[i][metricName];

		if (typeof value === 'number') {
			values.push(this.metrics[i][metricName]);
		}
	}

	// apply stats functions
	this.getAvailableStats().forEach(function(fnName) {
		stats[fnName] = parseFloat(this.stats[fnName](values));
	}, this);

	return stats;
};

stats.prototype.getAvailableStats = function() {
	return this.statsNames;
};

module.exports = stats;

/**
 * Results formatter for -R csv
 *
 * Options:
 *  no-header - omit CSV header
 *  timestamp - add the current timestamp as the first column
 *  url - add the URL as the first column
 *
 * @see https://github.com/touv/node-csv-string
 */
'use strict';

var CSV = require('csv-string');

module.exports = function(results, reporterOptions) {
	// public API
	return {
		render: function() {
			var metrics = results.getMetricsNames(),
				keys = [],
				values = [],
				ret = [];

			metrics.forEach(function(metric) {
				var value = results.getMetric(metric);

				keys.push(metric);
				values.push(value);
			});

			// -R csv:url
			if (reporterOptions.url === true) {
				values.unshift(results.getUrl());
				keys.unshift('url');
			}

			// -R csv:timestamp
			if (reporterOptions.timestamp === true) {
				values.unshift(new Date().toJSON().substr(0, 19));
				keys.unshift('timestamp');
			}

			// -R csv:no-header
			if (reporterOptions['no-header'] !== true) {
				ret.push(keys);
			}

			// add the values
			ret.push(values);

			return CSV.stringify(ret);
		}
	};
};

/**
 * Results formatter for -R json
 *
 * Options:
 *  pretty - pretty print the JSON
 */
'use strict';

module.exports = function(results, reporterOptions) {
	var isMultiple = Array.isArray(results);

	function formatSingleRunResults(results) {
		var res = {
			generator: results.getGenerator(),
			url: results.getUrl(),
			metrics: results.getMetrics(),
			offenders: results.getAllOffenders(),
			asserts: false
		};

		// add asserts
		var asserts = results.getAsserts(),
			failedAsserts;

		if (Object.keys(asserts).length > 0) {
			failedAsserts = results.getFailedAsserts();

			res.asserts = {
				rules: asserts,
				failedCount: failedAsserts.length,
				failedAsserts: failedAsserts,
			};
		}

		return res;
	}

	// public API
	return {
		handlesMultiple: true,
		render: function() {
			var res;

			if (!isMultiple) {
				res = formatSingleRunResults(results);
			} else {
				var stats = new(require('../lib/stats'))();

				res = {
					runs: results.map(function(run) {
						return formatSingleRunResults(run);
					}),
					stats: {}
				};

				// generate metrics stats (issue #285)
				for (var i = 0, runs = results.length; i < runs; i++) {
					stats.pushMetrics(results[i].getMetrics());
				}

				stats.getMetrics().forEach(function(metricName) {
					res.stats[metricName] = stats.getMetricStats(metricName);
				});
			}

			// -R json:pretty
			if (reporterOptions.pretty === true) {
				return JSON.stringify(res, null, 2);
			} else {
				return JSON.stringify(res);
			}
		}
	};
};

/**
 * Results formatter for --format=plain
 *
 * Options:
 *  no-color - disable ANSI colors
 */
'use strict';

var colors = require('../lib/ansicolors'),
	metrics = require('../lib/index').metadata.metrics,
	fold = require('travis-fold'),
	rpad = require('../core/pads').rpad,
	OK = '',
	ERR = '';

function getMetricUnit(metricName) {
	var entry = metrics[metricName];
	return (typeof entry !== 'undefined' && entry.unit !== 'number' && entry.unit !== 'string') ? entry.unit : false;
}

module.exports = function(results, reporterOptions) {
	var isMultiple = Array.isArray(results),
		noColor = (reporterOptions['no-color'] === true);

	function formatSingleRunResults(results) {
		var res = [];

		if (noColor) {
			colors.disable();
		}

		// header
		res.push(results.getGenerator() + ' metrics for <' + results.getUrl() + '>:');
		res.push('');

		// metrics
		fold.pushStart(res, 'metrics');

		results.getMetricsNames().forEach(function(metric) {
			var line = ' ' + metric + ': ' + results.getMetric(metric),
				unit = getMetricUnit(metric);

			if (unit !== false) {
				line += ' ' + colors.brightBlack(unit);
			}

			// check asserts
			if (results.hasAssertion(metric)) {
				if (results.assert(metric)) {
					line = colors.brightGreen(OK + line);
				} else {
					line = rpad(ERR + line, 50) + 'Assertion failed! Expected to be less than or equal: ' + results.getAssertion(metric);
					line = colors.brightRed(line);
				}
			} else {
				line = '*' + line;
			}

			res.push(line);
		});

		fold.pushEnd(res, 'metrics');
		res.push('');

		// offenders
		var offenders = results.getAllOffenders();
		fold.pushStart(res, 'offenders');

		Object.keys(offenders).forEach(function(metric) {
			var LIMIT = 50,
				offenders = results.getOffenders(metric),
				len = offenders.length;

			res.push(colors.brightGreen('Offenders for ' + metric + ' (' + results.getMetric(metric) + '):'));

			// limit the ammount of offenders emitted
			offenders.slice(0, LIMIT).forEach(function(msg) {
				res.push(' * ' + msg);
			});

			if (len > LIMIT) {
				res.push(colors.brightBlack('(' + (len - LIMIT) + ' more)'));
			}

			res.push('');
		});

		fold.pushEnd(res, 'offenders');

		return fold.wrap(
			results.getUrl(),
			res.join('\n').trim()
		) + '\n';
	}

	function formatMultipleRunResults(results) {
		var AsciiTable = require('ascii-table'),
			format = require('util').format,
			stats = new(require('../lib/stats'))(),
			runs = results.length,
			fields, table;

		// table title and heading
		table = new AsciiTable();
		table.setTitle(format('Report from %d run(s) for <%s> using %s', runs, results[0].getUrl(), results[0].getGenerator()));
		table.setTitleAlignLeft();

		var heading = [];
		heading.push(AsciiTable.alignCenter('Metric', 30));

		stats.getAvailableStats().forEach(function(name) {
			heading.push(AsciiTable.alignCenter(name, 12));
		});

		table.setHeading(heading);

		// metrics stats
		for (var i = 0; i < runs; i++) {
			stats.pushMetrics(results[i].getMetrics());
		}

		// generate rows (one for each metric)
		stats.getMetrics().forEach(function(metricName) {
			var row = [],
				metricStats = stats.getMetricStats(metricName),
				unit = getMetricUnit(metricName),
				heading = metricName;

			if (unit !== false) {
				heading += ' [' + unit + ']';
			}

			row.push(heading);

			Object.keys(metricStats).forEach(function(stat) {
				row.push(metricStats[stat]);
			});

			table.addRow(row);
		});

		return table.toString() + "\n";
	}

	// public API
	return {
		handlesMultiple: true,
		render: function() {
			if (!isMultiple) {
				return formatSingleRunResults(results);
			} else {
				return formatMultipleRunResults(results);
			}
		}
	};
};

/**
 * Results formatter for --format=statsd
 *
 * --reporter statsd
 * --statsd-host localhost
 * --statsd-port 8125
 * --statsd-prefix "myapp.frontPage."
 *
 * Options:
 *  <host>:<port>:<prefix>
 *
 * @see https://github.com/sivy/node-statsd
 */
'use strict';

var StatsD = require('node-statsd').StatsD;

module.exports = function(results, reporterOptions, options) {
	var client,
		debug = require('debug')('phantomas:reporter:statsd'),
		params;

	// -R statsd:<host>:<port>:<prefix>
	if (reporterOptions.length > 0) {
		options['statsd-host'] = reporterOptions[0];
		options['statsd-port'] = reporterOptions[1];
		options['statsd-prefix'] = reporterOptions[2];
	}

	params = {
		host: options['statsd-host'] || 'localhost',
		port: options['statsd-port'] || 8125,
		prefix: (typeof options['statsd-prefix'] === 'undefined') ? 'phantomas.' : options['statsd-prefix']
	};

	debug('Patameters: %j', params);

	// public API
	return {
		render: function(done) {
			var client = new StatsD(params),
				metrics = results.getMetricsNames(),
				remaining = metrics.length,
				bytesSent = 0;

			client.socket.on('error', function(error) {
				debug('Error in socket: %s', error);
			});

			metrics.forEach(function(metric) {
				var value = results.getMetric(metric);

				if (isNaN(parseFloat(value))) {
					debug('Not sending %s as "%s" is not a numeric metric', metric, value);
					remaining--;
				} else {
					//debug('Sending %s = %s...', metric, value);

					client.gauge(metric, value, function(err, bytes) {
						if (err) {
							debug('Error: %s', err);
						} else {
							bytesSent += bytes;
							remaining--;

							if (remaining === 0) {
								debug('All metrics sent (%d bytes)', bytesSent);
								done();
							}
						}
					});
				}
			});
		}
	};
};

/**
 * Results formatter for --format=tap
 *
 * Options:
 *  no-skip - don't print out metrics that were skipped
 *
 * @see http://podwiki.hexten.net/TAP/TAP.html?page=TAP
 * @see https://github.com/macbre/tap-producer
