			case 'domInteractive':
				phantomas.setMetric('requestsToDomContentLoaded', requests);
				setDomainMetric('domainsToDomContentLoaded');
				break;

			case 'domComplete':
				phantomas.setMetric('requestsToDomComplete', requests);
				setDomainMetric('domainsToDomComplete');
				break;
		}
	});
