			else if (entry.isCSS) {
				phantomas.incrMetric('smallCssFiles');
				phantomas.addOffender('smallCssFiles', '%s (%s kB)', entry.url, sizeFormatted);
			} else if (entry.isJS) {
				phantomas.incrMetric('smallJsFiles');
				phantomas.addOffender('smallJsFiles', '%s (%s kB)', entry.url, sizeFormatted);
			}
