	});

	phantomas.on('report', function() {
		assetsReqCounter.forEach(function(asset, cnt) {
			if (cnt > 1) {
				phantomas.incrMetric('multipleRequests');
				phantomas.addOffender('multipleRequests', asset);
			}
		});
	});
